// Filter campaigns based on filters
const campaigns = $input.first().json.results || [];
const filters = $('Prepare Dates').item.json.campaignFilters;
const configData = $('Prepare Dates').item.json;

const filteredCampaigns = [];

for (const filter of filters) {
  if (!filter.enabled) continue;

  for (const result of campaigns) {
    const campaignName = result.campaign.name;

    if (campaignName.includes(filter.contains)) {
      filteredCampaigns.push({
        json: {
          campaignId: result.campaign.id,
          campaignName: campaignName,
          filterType: filter.name,
          ...configData
        }
      });
      break; // Only match once per filter
    }
  }
}

if (filteredCampaigns.length === 0) {
  return [{
    json: {
      error: 'No campaigns found matching filters',
      country: configData.country
    }
  }];
}

return filteredCampaigns;

