// Simple comparison: Today vs Yesterday
const todayData = $('Get Today Performance').first().json.results?.[0] || {};
const yesterdayData = $('Get Yesterday Performance').first().json.results?.[0] || {};
const campaignData = $('Filter Campaigns').item.json;

// TODAY's metrics
const todayConversions = parseFloat(todayData.metrics?.conversions || 0);
const todayClicks = parseFloat(todayData.metrics?.clicks || 0);
const todayConvRate = todayClicks > 0 ? todayConversions / todayClicks : 0;

// YESTERDAY's metrics
const yesterdayConversions = parseFloat(yesterdayData.metrics?.conversions || 0);
const yesterdayClicks = parseFloat(yesterdayData.metrics?.clicks || 0);
const yesterdayConvRate = yesterdayClicks > 0 ? yesterdayConversions / yesterdayClicks : 0;

// Minimum clicks check
const minClicks = campaignData.minClicksRequired;
if (todayClicks < minClicks || yesterdayClicks < minClicks) {
  return [{
    json: {
      ...campaignData,
      skipReason: 'INSUFFICIENT_DATA',
      todayClicks: todayClicks,
      yesterdayClicks: yesterdayClicks,
      minRequired: minClicks,
      shouldApply: false
    }
  }];
}

// Calculate percentage difference
const convRateChange = yesterdayConvRate > 0 
  ? ((todayConvRate - yesterdayConvRate) / yesterdayConvRate) * 100 
  : 0;

const absChange = Math.abs(convRateChange);
const minChangeRequired = campaignData.minChangePercent;

// Check if change is meaningful
if (absChange < minChangeRequired) {
  return [{
    json: {
      ...campaignData,
      skipReason: 'CHANGE_TOO_SMALL',
      todayConvRate: (todayConvRate * 100).toFixed(2),
      yesterdayConvRate: (yesterdayConvRate * 100).toFixed(2),
      convRateChange: convRateChange.toFixed(2),
      minChangeRequired: minChangeRequired,
      shouldApply: false
    }
  }];
}

// Cap adjustment at max
let adjustmentPercent = convRateChange;
const maxAdjustment = campaignData.maxAdjustmentPercent;
adjustmentPercent = Math.max(-maxAdjustment, Math.min(maxAdjustment, adjustmentPercent));

// Calculate conversion rate modifier for Google Ads API
const conversionRateModifier = 1 + (adjustmentPercent / 100);

return [{
  json: {
    ...campaignData,
    shouldApply: true,
    todayConvRate: (todayConvRate * 100).toFixed(2),
    yesterdayConvRate: (yesterdayConvRate * 100).toFixed(2),
    convRateChange: convRateChange.toFixed(2),
    adjustmentPercent: Math.round(adjustmentPercent),
    conversionRateModifier: conversionRateModifier.toFixed(3),
    todayClicks: todayClicks,
    todayConversions: todayConversions,
    yesterdayClicks: yesterdayClicks,
    yesterdayConversions: yesterdayConversions
  }
}];

