const data = $input.first().json;

return [{
  json: {
    ...data,
    wasSkipped: true,
    message: `SKIPPED - ${data.skipReason}`
  }
}];

const allItems = $input.all();

const applied = allItems.filter(i => !i.json.dryRunResult && !i.json.wasSkipped && i.json.shouldApply);
const dryRun = allItems.filter(i => i.json.dryRunResult);
const skipped = allItems.filter(i => i.json.wasSkipped);

const updated = allItems.filter(i => i.json.shouldUpdate && !i.json.dryRunResult && !i.json.wasSkipped);
const created = allItems.filter(i => !i.json.shouldUpdate && !i.json.dryRunResult && !i.json.wasSkipped && i.json.shouldApply);

const summary = {
  timestamp: new Date().toISOString(),
  isDryRun: dryRun.length > 0,
  totalProcessed: allItems.length,
  adjustmentsApplied: applied.length,
  updated: updated.length,
  created: created.length,
  skipped: skipped.length
};

const details = allItems.map(item => ({
  campaignName: item.json.campaignName,
  todayConvRate: item.json.todayConvRate,
  yesterdayConvRate: item.json.yesterdayConvRate,
  change: item.json.convRateChange,
  adjustmentApplied: item.json.adjustmentPercent,
  action: item.json.wasSkipped ? 'SKIPPED' : 
          item.json.dryRunResult ? (item.json.shouldUpdate ? 'DRY RUN - UPDATE' : 'DRY RUN - CREATE') :
          item.json.shouldUpdate ? 'UPDATED' : 'CREATED',
  sbaName: item.json.sbaName || 'N/A',
  status: item.json.wasSkipped ? `SKIPPED - ${item.json.skipReason}` : 
          item.json.dryRunResult ? 'DRY RUN' : 'APPLIED',
  duration: item.json.adjustmentStartDateTime && item.json.adjustmentEndDateTime ?
    `${item.json.adjustmentStartDateTime} to ${item.json.adjustmentEndDateTime}` : null
}));

return [{
  json: {
    summary: summary,
    details: details
  }
}];
